package com.example.javaapp;

import org.springframework.web.bind.annotation.*;
import java.util.List;
import java.util.UUID;

@RestController
public class HelloController {

    private final KafkaService kafkaService;

    public HelloController(KafkaService kafkaService) {
        this.kafkaService = kafkaService;
    }

    @GetMapping("/")
    public String hello() {
        return "<!DOCTYPE html>\n<html lang=\"fr\">\n<head>\n    <meta charset=\"UTF-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n    <title>Java App - Pipeline CI/CD</title>\n    <style>\n        * {\n            margin: 0;\n            padding: 0;\n            box-sizing: border-box;\n        }\n        \n        body {\n            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;\n            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);\n            min-height: 100vh;\n            display: flex;\n            align-items: center;\n            justify-content: center;\n            color: #333;\n        }\n        \n        .container {\n            background: rgba(255, 255, 255, 0.95);\n            padding: 40px;\n            border-radius: 20px;\n            box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);\n            text-align: center;\n            max-width: 1000px;\n            width: 95%;\n            backdrop-filter: blur(10px);\n            border: 1px solid rgba(255, 255, 255, 0.2);\n        }\n        \n        h1 {\n            color: #667eea;\n            font-size: 3.5em;\n            margin-bottom: 20px;\n            font-weight: 700;\n            text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);\n        }\n        \n        .subtitle {\n            color: #666;\n            font-size: 1.2em;\n            margin-bottom: 30px;\n            line-height: 1.6;\n        }\n        \n        .status {\n            display: inline-block;\n            background: linear-gradient(45deg, #4CAF50, #45a049);\n            color: white;\n            padding: 10px 30px;\n            border-radius: 50px;\n            font-weight: 600;\n            margin-bottom: 25px;\n            box-shadow: 0 4px 15px rgba(76, 175, 80, 0.3);\n            animation: pulse 2s infinite;\n        }\n        \n        @keyframes pulse {\n            0% { transform: scale(1); }\n            50% { transform: scale(1.05); }\n            100% { transform: scale(1); }\n        }\n        \n        .features {\n            display: grid;\n            grid-template-columns: repeat(4, 1fr);\n            gap: 15px;\n            margin-top: 20px;\n        }\n        \n        .feature {\n            background: linear-gradient(45deg, #f093fb, #f5576c);\n            color: white;\n            padding: 15px;\n            border-radius: 12px;\n            font-weight: 500;\n            box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);\n            transition: transform 0.3s ease, background 0.3s;\n            border: none;\n            cursor: pointer;\n            font-size: 0.9em;\n        }\n        \n        .feature:hover {\n            transform: translateY(-3px);\n            filter: brightness(1.1);\n        }\n        \n        .feature-blue {\n            background: linear-gradient(45deg, #667eea, #764ba2);\n        }\n        \n        .java-logo {\n            font-size: 3em;\n            margin-bottom: 10px;\n            animation: bounce 2s infinite;\n        }\n        \n        @keyframes bounce {\n            0%, 20%, 50%, 80%, 100% { transform: translateY(0); }\n            40% { transform: translateY(-10px); }\n            60% { transform: translateY(-5px); }\n        }\n\n        .kafka-ctrl {\n            margin-top: 25px;\n            padding-top: 20px;\n            border-top: 1px solid #eee;\n        }\n\n        .kafka-input-group {\n            display: flex;\n            justify-content: center;\n            gap: 10px;\n            margin-bottom: 15px;\n            align-items: center;\n        }\n\n        .kafka-input-group input {\n            padding: 8px 12px;\n            border-radius: 8px;\n            border: 1px solid #ddd;\n            width: 80px;\n            text-align: center;\n            font-weight: bold;\n        }\n\n        .kafka-logs {\n            margin-top: 15px;\n            padding: 12px;\n            background: #272822;\n            color: #f8f8f2;\n            border-radius: 8px;\n            font-family: 'Consolas', monospace;\n            font-size: 0.8em;\n            height: 120px;\n            overflow-y: auto;\n            text-align: left;\n            border: 2px solid #3e3d32;\n        }\n\n        .log-entry { margin-bottom: 4px; border-bottom: 1px solid #3e3d32; padding-bottom: 2px; }\n        \n        .footer {\n            margin-top: 25px;\n            color: #888;\n            font-size: 0.85em;\n        }\n    </style>\n</head>\n<body>\n    <div class=\"container\">\n        <div class=\"java-logo\">‚òï</div>\n        <h1>Java App</h1>\n        <p class=\"subtitle\">Int√©gration Kafka K3s & GitOps</p>\n        \n        <div class=\"status\">‚úÖ Application en ligne</div>\n        \n        <div class=\"features\">\n            <div class=\"feature\">üöÄ Spring Boot</div>\n            <div class=\"feature\">üîÑ CI/CD Pipeline</div>\n            <div class=\"feature\">üê≥ Docker Ready</div>\n            <div class=\"feature\">‚òï Java 17+</div>\n        </div>\n\n        <div class=\"kafka-ctrl\">\n            <div class=\"kafka-input-group\">\n                <label>Messages :</label>\n                <input type=\"number\" id=\"msgCount\" value=\"100\" min=\"1\" max=\"10000\">\n            </div>\n            <div class=\"features\">\n                <button class=\"feature feature-blue\" onclick=\"sendKafka()\">üöÄ Envoi Kafka</button>\n                <button class=\"feature feature-blue\" onclick=\"readKafka()\">üì• Lecture Kafka</button>\n            </div>\n        </div>\n        \n        <div id=\"kafkaLogs\" class=\"kafka-logs\">Initialisation des logs...</div>\n        \n        <div class=\"footer\">\n            <p>Design original restaur√© ‚Ä¢ Int√©gration Kafka active</p>\n        </div>\n    </div>\n\n    <script>\n        async function sendKafka() {\n            const count = document.getElementById('msgCount').value;\n            const logBox = document.getElementById('kafkaLogs');\n            logBox.innerHTML = `<div class=\"log-entry\">Envoi de ${count} messages...</div>`;\n            \n            try {\n                const res = await fetch(`/api/kafka/send?count=${count}`, { method: 'POST' });\n                if (res.ok) {\n                    logBox.innerHTML += `<div class=\"log-entry\">Succ√®s : ${count} messages envoy√©s !</div>`;\n                    setTimeout(readKafka, 1000);\n                }\n            } catch (e) {\n                logBox.innerHTML += `<div class=\"log-entry\" style=\"color: #f92672\">Erreur d'envoi.</div>`;\n            }\n        }\n\n        async function readKafka() {\n            try {\n                const res = await fetch('/api/kafka/messages');\n                if (!res.ok) throw new Error();\n                const data = await res.json();\n                const logBox = document.getElementById('kafkaLogs');\n                if (data.length === 0) {\n                    logBox.innerHTML = '<div class=\"log-entry\">Aucun message dans Kafka.</div>';\n                } else {\n                    logBox.innerHTML = data.map(m => `<div class=\"log-entry\">${m}</div>`).join('');\n                }\n            } catch (e) {\n                document.getElementById('kafkaLogs').innerHTML = '<div class=\"log-entry\" style=\"color: #f92672\">Erreur de lecture. V√©rifiez le cluster Kafka.</div>';\n            }\n        }\n        \n        // Auto refresh every 5s\n        setInterval(readKafka, 5000);\n        readKafka();\n    </script>\n</body>\n</html>";
    }

    @PostMapping("/api/kafka/send")
    public String send(@RequestParam(defaultValue = "100") int count) {
        for (int i = 0; i < count; i++) {
            kafkaService.sendMessage("Log Data #" + (i+1) + " - " + UUID.randomUUID().toString().substring(0, 8));
        }
        return count + " messages sent to Kafka";
    }

    @GetMapping("/api/kafka/messages")
    public List<String> getMessages() {
        return kafkaService.getReceivedMessages();
    }

    @PostMapping("/api/kafka/clear")
    public String clear() {
        kafkaService.clearMessages();
        return "Messages cleared";
    }
}


